script:
frappe.ui.form.on('Serial No', {
    onload: function(frm) {
        frappe.call({
                method: 'sonali.custom_functions.Serial_No.generate_qr_for_missing_serials',  // Backend method to generate QR codes
                callback: function(r) {
                    if (!r.exc) {
                        // If successful, show a success message
                       console.log("Generating QR code")
                    } else {
                        // If there was an error, show an error message
                        frappe.msgprint(__('Error generating QR Codes.'));
                    }
                }
            });
    }
});

frappe.ui.form.on('Work Order', {
    before_save: function(frm) {
        // Loop through each row in the Operations child table
        frm.doc.operations.forEach(row => {
            if (!row.workstation) { // Only set if workstation is not already defined
                row.workstation = "Work - 3"; // Replace with your default workstation name
            }
        });
        frm.refresh_field('operations'); // Refresh the table to reflect changes
    }
});
