frappe.ui.form.on('Delivery Note', {
    refresh: function(frm) {
        if (!frm.fields_dict.batch_scan) {
            frm.add_custom_button(__('Scan Batch'), function() {
                frappe.prompt({
                    label: 'Scan or Enter Batch No',
                    fieldname: 'batch_no',
                    fieldtype: 'Data'
                }, function(values) {
                    if (values.batch_no) {
                        fetch_and_scan_serials(frm, values.batch_no);
                    }
                }, __('Batch Scan'), __('OK'));
            });
        }
    }
});

// Fetch serial numbers linked to the batch and simulate scanning
function fetch_and_scan_serials(frm, batch_no) {
    frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Serial No",
            filters: { batch_no: batch_no, status: "Active" }, // Fetch only active serials
            fields: ["name"],
            limit_page_length: 100
        },
        callback: function(response) {
            if (response.message.length) {
                let serial_nos = response.message.map(s => s.name);
                
                // Simulate scanning each serial number one by one
                serial_nos.forEach((serial, index) => {
                    setTimeout(() => {
                        frm.set_value("scan_barcode", serial);
                        frm.script_manager.trigger("scan_barcode");
                    }, index * 500); // Delay each scan by 500ms to mimic manual scanning
                });

                frappe.msgprint(`Simulating scan of ${serial_nos.length} serial numbers from Batch No ${batch_no}.`);
            } else {
                frappe.msgprint(`No active serial numbers found for Batch No ${batch_no}.`);
            }
        }
    });
}

