frappe.ui.form.on("Sales Invoice", {
    after_save: function(frm) {
        if (frm.doc.docstatus !== 0) {  // Prevent updating submitted invoices
            frappe.msgprint(__("Cannot update a submitted Sales Invoice"));
            return;
        }

        frappe.call({
            method: "sonali.custom_functions.get_delivery_note_weights.get_delivery_note_weights",
            args: {
                sales_invoice: frm.doc.name
            },
            callback: function(response) {
                console.log("Response from Server:", response);

                if (response.message && response.message.updated_items) {
                    frm.set_value("items", []);  // Clear existing items

                    response.message.updated_items.forEach(item => {
                        let child = frm.add_child("items");
                        child.item_code = item.item_code;
                        child.custom_weight_in_kg = item.custom_weight_in_kg;
                        child.rate = item.rate;
                    });

                    frm.refresh_field("items");
                } else {
                    console.warn("No updated items found in response");
                }
            },
            error: function(error) {
                console.error("Error fetching delivery note weights:", error);
                frappe.msgprint(__("Failed to fetch delivery note weights."));
            }
        });
    },
    onload: function(frm) {
        if (frm.doc.__islocal && frm.doc.items.length > 0) {
            frappe.call({
                method: "sonali.custom_functions.get_delivery_note_weights.get_delivery_note_weights",
                args: {
                    sales_invoice: frm.doc.name
                },
                callback: function(response) {
                    console.log("Response from Server:", response);

                    if (response.message && response.message.updated_items) {
                        frm.set_value("items", []);  // Clear existing items

                        response.message.updated_items.forEach(item => {
                            let child = frm.add_child("items");
                            child.item_code = item.item_code;
                            child.custom_weight_in_kg = item.custom_weight_in_kg;
                            child.rate = item.rate;
                        });

                        frm.refresh_field("items");
                    } else {
                        console.warn("No updated items found in response");
                    }
                },
                error: function(error) {
                    console.error("Error fetching delivery note weights:", error);
                    frappe.msgprint(__("Failed to fetch delivery note weights."));
                }
            });
        }
    }
});

