script:
frappe.ui.form.on('Work Order', {
    before_save: function(frm) {
        // Loop through each row in the Operations child table
        frm.doc.operations.forEach(row => {
            if (!row.workstation) { // Only set if workstation is not already defined
                row.workstation = "Work - 3"; // Replace with your default workstation name
            }
        });
        frm.refresh_field('operations'); // Refresh the table to reflect changes
    }
});

frappe.ui.form.on("Job Card", {
    refresh: function(frm) {
        let operation = frm.doc.operation;
        if (operation === "Packaging") {
                group = "Packaging Team";
            } 
        else if (operation === "Annealing") {
                group = "Annealing Team";
            } 
        else if (operation === "Bunching") {
                group = "Bunching Team";
            } 
        else if (operation === "Insulation") {
                group = "Insulation Team";
            }
        frm.set_query('employee', () => {
    return {
        filters: {
            custom_employee_group: group
        }
    };
        });
    }
});

frappe.listview_settings['Bulk Coupon'] = {
    onload: function(listview) {
        listview.page.add_inner_button(__('Create Bulk Data'), function() {
            frappe.prompt([
                {
                    fieldname: 'number_of_coupons',
                    label: 'Number of Coupons',
                    fieldtype: 'Int',
                    reqd: 1
                },
                {
                    fieldname: 'coupon_amount',
                    label: 'Coupon Amount',
                    fieldtype: 'Int',
                    reqd: 1
                }
            ], function(values) {
                frappe.call({
                    method: 'sonali.custom_functions.Bulk_coupon.create_bulk_data',
                    args: {
                        number_of_coupons: values.number_of_coupons,
                        coupon_amount: values.coupon_amount
                    },
                    callback: function(response) {
                        if (response.message) {
                            frappe.msgprint(__('Bulk data created successfully!'));
                            listview.refresh();
                        }
                    }
                });
            }, __('Create Bulk Data'), __('Create'));
        });
    }
};

frappe.ui.form.on('Work Order', {
    refresh: function(frm) {
        frm.add_custom_button(__('Create Batches'), function() {
            frappe.call({
                method: "sonali.custom_functions.batch_customization.create_batches",
                args: { work_order: frm.doc.name },
                callback: function(r) {
                    if (r.message) {
                        frappe.msgprint(__('Batches Created: ' + r.message.join(', ')));
                    }
                }
            });
        });
    }
});
