script:
frappe.ui.form.on('Serial No', {
    onload: function(frm) {
        frappe.call({
                method: 'sonali.custom_functions.Serial_No.generate_qr_for_missing_serials',  // Backend method to generate QR codes
                callback: function(r) {
                    if (!r.exc) {
                        // If successful, show a success message
                       console.log("Generating QR code")
                    } else {
                        // If there was an error, show an error message
                        frappe.msgprint(__('Error generating QR Codes.'));
                    }
                }
            });
    }
});
