html:
{% set company = frappe.get_doc("Company", doc.company) %}
<!DOCTYPE html>
<html>
<head>
    <style>
        body { font-family: Arial, sans-serif; }
        .header { text-align: center; }
        .header img { width: 300px;}
        .company-details { text-align: center; font-size: 12px; margin-bottom: 10px; }
        .title { font-size: 18px; font-weight: bold; text-align: center; margin-bottom: 2px; }
        .table { width: 99.80%; border-collapse: collapse;  margin-top: 2px;}
        .table, .table th, .table td { border: 1px solid black; }
        .table th, .table td { text-align: center;}
        .subtotal { font-weight: bold; }
        @media print {
        body {
            margin-top: 0;
            padding-top: 0;
        }
    }

    </style>
</head>
<body>
    <div class="header">
        <img src="{{ company.default_letter_head or '/private/files/SW LLP.png' }}" alt="Company Logo">
    </div>
    <div class="company-details">
        <strong>
            SF No. 289/1B & 289/2B, Solavampalayam Village, Kinathukadavu Taluk,<br>
            Coimbatore , Tamil Nadu- 642109, INDIA<br>
        </strong>
        GSTIN: 33ADPFS3520F1Z7 | PAN: ADPFS3520F <br>
        Contact: 8344422211 | info@ sonaligroup.com | www.sonaligroup.com
    </div>

    <div class="title" style="border: 1px solid #000">Packing Slip</div>

    <table width="100%">
        <tr>
            <td><strong>Invoice No:</strong> {{ doc.name }}</td>
            <td><strong>No:</strong> {{ doc.naming_series }}</td>
        </tr>
        <tr>
            <td><strong>Vehicle No:</strong> {{ doc.vehicle_no or "N/A" }}</td>
            <td><strong>Date:</strong> {{ doc.posting_date }}</td>
        </tr>
        <tr>
            <td colspan="2"><strong>PO No:</strong> {{ doc.po_no or "N/A" }}</td>
        </tr>
    </table>
    {% set grouped_items = {} %}
{% set ns = namespace(total_length=0) %}

{% for item in doc.items %}
    {% if item.serial_no %}
        {% set length = frappe.db.get_value("Serial No", item.serial_no, "custom_length") or 0 %}
    {% else %}
        {% set length = 0 %}
    {% endif %}

    {% if item.item_code not in grouped_items %}
        {% set _ = grouped_items.update({item.item_code: {"size": frappe.db.get_value("Item", item.item_code, "custom_size_mm") or "N/A", "items": [], "total_length": 0}}) %}
    {% endif %}

    {% set _ = grouped_items[item.item_code]["items"].append({"serial_no": item.serial_no or "N/A", "length": length, "item_code": item.item_code}) %}
    {% set _ = grouped_items[item.item_code].update({"total_length": grouped_items[item.item_code]["total_length"] + length}) %}
    {% set ns.total_length = ns.total_length + length %}
{% endfor %}



    <table class="table">
        <thead>
            <tr>
                <th>#</th>
                <th>Product Name</th>
                <th>Size</th>
                <th>Box No</th>
                <th>Length</th>
            </tr>
        </thead>
        {% set ns = namespace(row_index=0, total_length=0) %}
        <tbody>
            {% for item_code, details in grouped_items.items() %}
                {% for item in details["items"] %}
                    {% set ns.row_index = ns.row_index + 1 %}
                    {% set ns.total_length = ns.total_length + item.length %}  
                    <tr>
                        <td>{{ ns.row_index }}</td>  
                        <td>{{ item.item_code }}</td>
                        <td>{{ details.size }}</td>
                        <td>{{ item.serial_no }}</td>
                        <td>{{ "%.3f"|format(item.length) }}</td>
                    </tr>
                {% endfor %}
                <tr class="subtotal">
                    <td colspan="4" style="text-align: right;">Subtotal:</td>
                    <td>{{ "%.3f"|format(details.total_length) }}</td>
                </tr>
            {% endfor %}
            <tr class="subtotal">
                <td colspan="4" style="text-align: right;">Total Length:</td>
                <td>{{ "%.3f"|format(ns.total_length) }}</td>  
            </tr>
        </tbody>

    </table>
</body>
</html>
