css:
.work-order-print {
    font-family: Arial, sans-serif;
    font-size: 12px;
    color: #000;
}
.work-order-header {
    text-align: right;
    font-weight: bold;
}
.work-order-title {
    text-align: right;
    font-size: 16px;
    margin-bottom: 5px;
}
.work-order-section {
    margin: 10px 0;
}
.work-order-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 10px;
}
.work-order-table th, .work-order-table td {
    border: 1px solid #000;
    padding: 4px;
    text-align: center;
}
.work-order-details-table {
    width: 100%;
    border-collapse: collapse;
    border: 1px solid #000;
    float: left;
    margin-top: 10px;
}

.work-order-details-table td {
    border: 1px solid #000;
    padding: 6px 8px;
    text-align: left;
    vertical-align: top;
}
